mapper.select:
SELECT *
FROM (
  SELECT ID, PLATE_FULL, ENTRY_AT, IMAGE_URL
  FROM VEHICLE_ENTRY
  WHERE PLATE_FULL = #{plateFull}
    AND STATUS = 'IN'
  ORDER BY ENTRY_AT DESC
)
WHERE ROWNUM <= #{limit}

SELECT *
FROM (
  SELECT ID, PLATE_FULL, ENTRY_AT, IMAGE_URL
  FROM VEHICLE_ENTRY
  WHERE PLATE_LAST4 = #{digits}
    AND STATUS = 'IN'
  ORDER BY ENTRY_AT DESC
)
WHERE ROWNUM <= #{limit}
